mapper.insert:
INSERT INTO BOARD (
	BOARD_NO
	, BOARD_TITLE
	, BOARD_CONTENT
	, IS_SECRET
	, CATE_NO
) VALUES (
	(SELECT 'BOARD_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BOARD_NO, 7))), 0) + 1, 3, '0') 
         FROM BOARD)
	, #{boardTitle}
	, #{boardContent}
	, #{isSecret}
	, #{cateNo}
)

mapper.select:
SELECT BOARD_READ_CNT
FROM BOARD
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
SET BOARD_READ_CNT = #{boardReadCnt} + 1
WHERE BOARD_NO = #{boardNo}
