mapper.select:
SELECT EMP_NO
FROM EMP
WHERE MEM_NO = #{memNo}

mapper.insert:
INSERT INTO LECTURE (
	LEC_NO
	, LEC_NAME
	, LEC_SCORE
	, MAX_NUM
	, NOW_NUM
	, EMP_NO
	, SEM_NO
	, COLL_NO
	, DEPT_NO
) VALUES (
	#{lecNo}
	, #{lecName}
	, #{lecScore}
	, #{maxNum}
	, #{nowNum}
	, #{empNo}
	, #{semNo}
	, #{collNo}
	, #{deptNo}
)

INSERT INTO LECTURE_TIME (
	TIME_NO
	, LEC_DAY
	, FIRST_TIME
	, LAST_TIME
	, LEC_NO
) 

	SELECT (SELECT 'TIME_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(TIME_NO, 6))), 0) +#{i}+1,3,0) 
			FROM LECTURE_TIME)
		, #{lecTime.lecDay}
		, #{lecTime.firstTime}
		, #{lecTime.lastTime}
		, #{lecTime.lecNo}
	FROM DUAL

mapper.select:
SELECT LEC.LEC_NO , LEC_NAME , LEC_SCORE , FIRST_TIME , LAST_TIME , LEC_DAY , DEPT_NAME , COLL_NAME , MEM_NAME , MAX_NUM , NOW_NUM , DECODE(LEC_STATUS, 'Y', '강의중', 'N', '폐강') LEC_STATUS FROM LECTURE LEC, LECTURE_TIME TIME, DEPT, EMP, MEMBER, COLLEAGE COLL WHERE LEC.LEC_NO = TIME.LEC_NO AND LEC.DEPT_NO = DEPT.DEPT_NO AND LEC.COLL_NO = COLL.COLL_NO AND MEMBER.MEM_NO = EMP.MEM_NO AND LEC.EMP_NO = EMP.EMP_NO AND EMP.EMP_NO = #{empNo}
AND UPPER(LEC_NAME) LIKE '%'||UPPER(#{searchValue})||'%'
AND LEC_STATUS = 'Y'
AND LEC_STATUS = 'N'
AND TIME.LEC_NO = #{lecNo}

mapper.update:
UPDATE LECTURE SET 
	LEC_STATUS = 'N'
WHERE LEC_NO = #{lecNo}

mapper.select:
	SELECT SUM(COUNT)
	FROM(

	    SELECT COUNT(TIME_NO) AS COUNT
	    FROM LECTURE_TIME TIME, LECTURE LEC
	    WHERE LEC_DAY = #{lecTime.lecDay}
	    AND ((FIRST_TIME <= #{lecTime.firstTime} AND LAST_TIME >= #{lecTime.firstTime}) 
	    OR (FIRST_TIME <= #{lecTime.lastTime} AND LAST_TIME >= #{lecTime.lastTime})) 
	    AND TIME.LEC_NO = LEC.LEC_NO
	    AND LEC.EMP_NO = #{empNo}

    )

SELECT LECTURE.LEC_NO , LEC_NAME , LEC_SCORE , CREATE_DATE , MAX_NUM , NOW_NUM , LEC_STATUS , LECTURE.EMP_NO , SEM_NO , LECTURE.COLL_NO , COLL_NAME , LECTURE.DEPT_NO , DEPT_NAME , LEC_DAY , FIRST_TIME , LAST_TIME , EMP.MEM_NO , MEM_NAME FROM LECTURE, LECTURE_TIME, COLLEAGE COLL, DEPT, MEMBER, EMP WHERE LECTURE.LEC_NO = LECTURE_TIME.LEC_NO AND LECTURE.COLL_NO = COLL.COLL_NO AND LECTURE.DEPT_NO = DEPT.DEPT_NO AND EMP.COLL_NO = LECTURE.COLL_NO AND MEMBER.MEM_NO = EMP.MEM_NO
AND UPPER(LEC_NAME) LIKE '%'||UPPER(#{searchValue})||'%'
AND COLL.COLL_NO = #{collNo}
AND DEPT.DEPT_NO = #{deptNo}

mapper.insert:
		INSERT INTO ENROLLMENT (
   				 ENROLLMENT_NO
   				 , STU_NO
  				 , LEC_NO
  				 , SEM_NO
				 ) VALUES (
				 (SELECT 'ENR_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ENROLLMENT_NO, 5))), 0) +1,3,0) FROM ENROLLMENT)
   				 , #{stuNo}
   				 , #{lecNo}
   				 , #{semNo}
   				 )

mapper.select:
SELECT COUNT(ENROLLMENT_NO) AS ERN
FROM ENROLLMENT
WHERE STU_NO = #{stuNo} 
AND LEC_NO = #{lecNo}

mapper.update:
UPDATE LECTURE
SET NOW_NUM = NOW_NUM + 1
WHERE LEC_NO = #{lecNo}

mapper.delete:
DELETE ENROLLMENT
WHERE STU_NO = #{stuNo}
AND LEC_NO = #{lecNo}

mapper.update:
UPDATE LECTURE
SET NOW_NUM = NOW_NUM - 1
WHERE LEC_NO = #{lecNo}
