mapper.select:
SELECT COUNT(MEM_NO)
FROM MEMBER
WHERE MEM_NO = #{memNo}

SELECT MEM_EMAIL
FROM MEMBER
WHERE MEM_NO = #{memNo}
AND MEM_NAME = #{memName}
AND MEM_QUEST = #{memQuest}

mapper.update:
UPDATE MEMBER
SET
	MEM_PW = #{memPw}
WHERE MEM_NO = #{memNo}
AND MEM_NAME = #{memName}

mapper.select:
SELECT MEM_EMAIL
FROM MEMBER
WHERE MEM_NAME = #{memName}
AND MEM_EMAIL = #{memEmail}

mapper.update:
UPDATE MEMBER
SET MEM_NAME = #{memName}
    , MEM_EMAIL = #{memEmail}
    , MEM_TELL = #{memTell}
    , MEM_ADDR = #{memAddr}
    , MEM_ADDR_DETAIL = #{memAddrDetail}
WHERE MEM_NO = #{memNo}
