mapper.insert:
		INSERT INTO SCHOOL_INFORMATION (
			SCH_INFO_CODE
			, SCH_INFO_TITLE
			, SCH_INFO_CONTENT
			, SCH_INFO_STARTDATE
			, SCH_INFO_ENDDATE
			, SCH_INFO_WRITER
			, SCH_INFO_NUM
		) VALUES (
			(SELECT 'SCH_CODE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(SCH_INFO_CODE, 10))), 0) + 1, 3, '0') 
         	FROM SCHOOL_INFORMATION)
			, #{schInfoTitle}
			, #{schInfoContent}
			, #{schInfoStartDate}
			, #{schInfoEndDate}
			, #{schInfoWriter}
			, (SELECT 'No.'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(SCH_INFO_NUM, 4))), 0) + 1, 2, '0') 
         	FROM SCHOOL_INFORMATION)
		)

mapper.update:
UPDATE SCHOOL_INFORMATION
SET 
	SCH_INFO_CONTENT = #{schInfoContent}
	, SCH_INFO_TITLE = #{schInfoTitle}
WHERE SCH_INFO_CODE = #{schInfoCode}

mapper.select:
SELECT STU_NO , STU_YEAR , STU_SEM , DECODE(STU_STATUS, '1', '재학중', '2','휴학중', '3', '제적') STU_STATUS , ST.MEM_NO , MEM_NAME , COLL.COLL_NO , COLL_NAME , DP.DEPT_NO , DEPT_NAME FROM STU ST, COLLEAGE COLL, DEPT DP, MEMBER MEM WHERE ST.COLL_NO = COLL.COLL_NO AND ST.DEPT_NO = DP.DEPT_NO AND ST.MEM_NO = MEM.MEM_NO
AND UPPER(MEM_NAME) LIKE '%'||UPPER(#{searchValue})||'%'
AND COLL.COLL_NO = #{collNo}
AND DP.DEPT_NO = #{deptNo}
OFFSET #{offsetCnt} ROWS FETCH NEXT #{displayCnt} ROWS ONLY

SELECT COUNT(STU_NO) FROM STU ST, COLLEAGE COLL, DEPT DP, MEMBER MEM WHERE ST.COLL_NO = COLL.COLL_NO AND ST.DEPT_NO = DP.DEPT_NO AND ST.MEM_NO = MEM.MEM_NO
AND UPPER(MEM_NAME) LIKE '%'||UPPER(#{searchValue})||'%'
AND COLL.COLL_NO = #{collNo}
AND DP.DEPT_NO = #{deptNo}

SELECT EMP_NO , EM.MEM_NO , MEM_NAME , DECODE(MEM_ROLE, 'pro', '교수', 'stf', '교직원') MEM_ROLE , EM.DEPT_NO , DEPT_NAME , EM.COLL_NO , COLL_NAME FROM EMP EM, MEMBER MEM, COLLEAGE COLL, DEPT DP WHERE EM.MEM_NO = MEM.MEM_NO AND EM.DEPT_NO = DP.DEPT_NO AND EM.COLL_NO = COLL.COLL_NO
AND UPPER(MEM_NAME) LIKE UPPER('%'||#{searchValue} ||'%')
AND COLL.COLL_NO = #{collNo}
AND DP.DEPT_NO = #{deptNo}

SELECT STATUS_NO , MB.MEM_NO , MEM_NAME , ST.STU_NO , STU_YEAR , DE.DEPT_NO , DEPT_NAME , COLL.COLL_NO , COLL_NAME , TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') AS APPLY_DATE , DECODE(ING_STATUS, 'I', '승인대기', 'P', '승인완료', 'R', '승인취소') AS ING_STATUS , NOW_STATUS , AFTER_STATUS FROM MEMBER MB, STU ST, COLLEAGE COLL, DEPT DE, STATUS_INFO SI WHERE ST.STU_NO = SI.STU_NO AND ST.DEPT_NO = DE.DEPT_NO AND ST.COLL_NO = COLL.COLL_NO AND MB.MEM_NO = ST.MEM_NO AND NOW_STATUS = 'E' AND AFTER_STATUS ='A' AND ING_STATUS = 'I'
AND TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') > #{searchFromDate}
AND TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') < #{searchToDate}
AND ${searchKeyword} LIKE '%'||UPPER(#{searchValue})||'%'
ORDER BY STATUS_NO DESC OFFSET #{offsetCnt} ROWS FETCH NEXT #{displayCnt} ROWS ONLY

SELECT STU_NO
FROM STATUS_INFO
WHERE STATUS_NO = #{statusNo}

