mapper.select:
SELECT APPLY_NO , TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') APPLY_DATE , DECODE(PROCESS_STATUS, 'I', '승인대기', 'P', '승인완료', 'R', '승인취소') AS PROCESS_STATUS , STU.STU_YEAR , STU.STU_SEM , STU.STU_NO , TO_CHAR(APPROVAL_DATE, 'YYYY-MM-DD') APPROVAL_DATE , MULTI_MAJOR_COLL , MULTI_MAJOR_DEPT , STU.COLL_NO , STU.DEPT_NO , COLL.COLL_NAME , DEPT.DEPT_NAME , MEM_NAME , STU.MEM_NO , APPLY_REASON FROM DEPT_MANAGE, STU, COLLEAGE COLL, DEPT, MEMBER MEM WHERE PROCESS_STATUS = 'I' AND MEM.MEM_NO = STU.MEM_NO AND STU.STU_NO = DEPT_MANAGE.STU_NO AND COLL.COLL_NO = DEPT_MANAGE.MULTI_MAJOR_COLL AND DEPT.DEPT_NO = DEPT_MANAGE.MULTI_MAJOR_DEPT
AND TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') > #{searchFromDate}
AND TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') < #{searchToDate}
AND ${searchKeyword} LIKE '%'||UPPER(#{searchValue})||'%'
ORDER BY APPLY_NO DESC OFFSET #{offsetCnt} ROWS FETCH NEXT #{displayCnt} ROWS ONLY

SELECT STU_NO
FROM DEPT_MANAGE
WHERE APPLY_NO = #{applyNo}

SELECT STU.STU_NO , STU_YEAR , STU_SEM , STU.MEM_NO , MEM_NAME , COLL.COLL_NO , COLL_NAME , DP.DEPT_NO , DEPT_NAME , SEM_NO , SEM_YEAR , SEMESTER FROM STU STU, COLLEAGE COLL, DEPT DP, MEMBER MEM, SEMESTER SEM WHERE STU.COLL_NO = COLL.COLL_NO AND STU.DEPT_NO = DP.DEPT_NO AND STU.MEM_NO = MEM.MEM_NO AND STU.STU_YEAR = SEM.SEM_YEAR AND STU.STU_SEM = SEMESTER
AND UPPER(MEM_NAME) LIKE '%'||UPPER(#{searchValue})||'%'
AND COLL.COLL_NO = #{collNo}
AND DP.DEPT_NO = #{deptNo}

