mapper.insert:
INSERT INTO STATUS_INFO (
	STATUS_NO
	, STU_NO
	, STATUS_CONTENT
	, AFTER_STATUS
) VALUES (
	(SELECT 'STATUS_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(STATUS_NO, 8))), 0) + 1, 3, '0') 
    FROM STATUS_INFO)
    , #{stuNo}
    , #{statusContent}
    , 'A'
)

INSERT INTO DEPT_MANAGE (
	APPLY_NO
	, APPLY_REASON
	, STU_YEAR
	, STU_SEM
	, STU_NO
	, MULTI_MAJOR_COLL
	, MULTI_MAJOR_DEPT
) VALUES (
	(SELECT 'APPLY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(APPLY_NO, 7))), 0) + 1, 3, '0') 
    FROM DEPT_MANAGE)
    , #{applyReason}
    , #{stuYear}
    , #{stuSem}
    , #{stuNo}
    , #{multiMajorColl}
    , #{multiMajorDept}
)
